import React, { Component } from 'react'
import { Text, View, StyleSheet, SafeAreaView, Modal, Image, AsyncStorage, RefreshControl, TouchableHighlight, ImageBackground } from 'react-native'
import { ScrollView } from 'react-native-gesture-handler';
import { Button, Icon, Left, Right, Header, Container, Body, Content } from 'native-base'
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons'
import { DrawerActions } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import FollowPage from './FollowPage';
import FollowList from './FollowList';
import Toast from 'react-native-simple-toast';
const UrlOfFile = "http://185.60.170.14/plesk-site-preview/ruppinmobile.ac.il/site10/uploadfiles/"
const Stack = createStackNavigator();
class ProfilePage extends Component {

  constructor(props) {
    super(props);
    this.state = {
      followers: 0,
      following: 0,
      uploaded: 0,
      username: '',
      email: '',
      openmodal: false,
      modalopen: false,
      refresh: false,
      newuri: 'data:image/png;base64,',
      image: 'data:image/png;base64,',
      imagelist: [{
        Photo_ID: 0,
        Photo_Date: "Theres No Date!",
        Photo_Uri: 'data:image/png;base64,',
        User_Email: "NO EMAIL",
        Photo_Status: "unchecked"
      }, {
        Photo_ID: 1,
        Photo_Date: "Theres No Date!",
        Photo_Uri: 'data:image/png;base64,',
        User_Email: "NO EMAIL",
        Photo_Status: "unchecked"
      }, {
        Photo_ID: 2,
        Photo_Date: "Theres No Date!",
        Photo_Uri: 'data:image/png;base64,',
        User_Email: "NO EMAIL",
        Photo_Status: "unchecked"
      }],
    }
  }
  componentWillUnmount() {
    this._unsubscribeFocus();
    this._unsubscribeBlur();
    this.setState({ openmodal: false })
  }
  showPhoto = async (uri) => {
    if (uri == 'data:image/png;base64,') {
    this.props.navigation.navigate('ImagesPage',{screen:'UploadPage'});  


    }
    else {

      var xUri = UrlOfFile + uri;
      this.setState({ newuri: xUri }, () => {
        this.setState({ openmodal: true });
      });
    }
  }
  async getStuff() {
    let user = await AsyncStorage.getItem('currentuser');
    let user2 = await JSON.parse(user)
    let Email = user2.User_Email;
    let username = user2.User_Name;
    let image = user2.User_Image;
    if (!image.includes("https://platform-lookaside")) {
      image = UrlOfFile + image;
    }
    this.setState({ image });
    //this.props.navigation.setOptions({ title: username + "'s Profile" })

    this.setState({
      username, email: Email
    });

    var ImageCountURL = 'http://185.60.170.14/plesk-site-preview/ruppinmobile.ac.il/site10/GetImageCount/' + Email + "/";
    await fetch(ImageCountURL).then((response) => {
      return response.json();
    }).then(async (data) => {

      this.setState({ uploaded: data });
    });

    var FollowingCountURL = "http://185.60.170.14/plesk-site-preview/ruppinmobile.ac.il/site10/howmanyifollow/" + Email + "/";

    await fetch(FollowingCountURL).then((resp) => {
      return resp.json();
    }).then((data) => {
      this.setState({ following: data });
    });
    var FollowersCountURL = "http://185.60.170.14/plesk-site-preview/ruppinmobile.ac.il/site10/howmanyfollowme/" + Email + "/";
    await fetch(FollowersCountURL).then((resp) => {
      return resp.json();
    }).then((data) => {
      this.setState({ followers: data });
    });

    var URLOFIMAGES = "http://185.60.170.14/plesk-site-preview/ruppinmobile.ac.il/site10/GetImagesByEmail/" + Email + "/";
    var Images;
    await fetch(URLOFIMAGES).then((resp) => {
      return resp.json();
    }).then((data) => {
      Images = data;
    });
    if (Images.Message == "This user didnt upload any picture yet.") {
      var arr = [{
        Photo_ID: 0,
        Photo_Date: "Theres No Date!",
        Photo_Uri: 'data:image/png;base64,',
        User_Email: "NO EMAIL",
        Photo_Status: "unchecked"
      }, {
        Photo_ID: 1,
        Photo_Date: "Theres No Date!",
        Photo_Uri: 'data:image/png;base64,',
        User_Email: "NO EMAIL",
        Photo_Status: "unchecked"
      }, {
        Photo_ID: 2,
        Photo_Date: "Theres No Date!",
        Photo_Uri: 'data:image/png;base64,',
        User_Email: "NO EMAIL",
        Photo_Status: "unchecked"
      }];
      this.setState({ imagelist: arr, refresh: false });
    }
    else {
      var len = Images.length;

      if (Images.length < 3) {
        for (var i = 0; i < 3 - len; i++) {
          var newobj = {
            Photo_ID: i,
            Photo_Date: "Theres No Date!",
            Photo_Uri: 'data:image/png;base64,',
            User_Email: "NO EMAIL",
            Photo_Status: "unchecked"
          };
          Images.push(newobj);
        }
      }

      this.setState({ imagelist: Images, refresh: false }, () => {
        this.setState({ refresh: false });
      });
    }






  }
  async componentDidMount() {

    this._unsubscribeFocus = this.props.navigation.addListener('focus', async (payload) => {
      this.setState({ openmoda: false }, () => {
        this.getStuff();
      })


    });
    this._unsubscribeBlur = this.props.navigation.addListener('blur', (payload) => {
      this.setState({ st: 'blur' })
    });
  }

  getfollowing = async () => {
    var url = "http://185.60.170.14/plesk-site-preview/ruppinmobile.ac.il/site10/Whoifollow/" + this.state.email + "/";

    await fetch(url).then((resp) => {
      return resp.json();
    }).then(async (data) => {
      var DataArray = data;
      var UrlUser = "http://185.60.170.14/plesk-site-preview/ruppinmobile.ac.il/site10/getUserByEmail/"
      var newArray = [];
      for (var i = 0; i < DataArray.length; i++) {
        await fetch(UrlUser + DataArray[i].followname + "/").then((resp) => {
          return resp.json();
        }).then((data) => {
          data.followdate = DataArray[i].followdate;
          newArray.push(data);
        });
      }

      this.props.navigation.navigate("FollowList", { List: newArray, Status: "Following Since: " });
    });


  }

  getfollowers = async () => {
    var url = "http://185.60.170.14/plesk-site-preview/ruppinmobile.ac.il/site10/whofollowsme/" + this.state.email + "/";

    await fetch(url).then((resp) => {
      return resp.json();
    }).then(async (data) => {


      var DataArray = data;
      var UrlUser = "http://185.60.170.14/plesk-site-preview/ruppinmobile.ac.il/site10/getUserByEmail/"
      var newArray = [];
      for (var i = 0; i < DataArray.length; i++) {
        await fetch(UrlUser + DataArray[i].followname + "/").then((resp) => {
          return resp.json();
        }).then((data) => {
          data.followdate = DataArray[i].followdate;

          newArray.push(data);
        });
      }

      this.props.navigation.navigate("FollowList", { List: newArray, Status: 'Following you Since: ' });

    })
  }
  render() {
    return (

      <Container >
        <Modal visible={this.state.openmodal} >
          <Header style={{ backgroundColor: 'white' }}>
            <Left style={{ flex: 1 }}>
              <Button onPress={() => {
                this.setState({ openmodal: false })
              }} style={{ backgroundColor: 'white', color: 'blue' }} transparent >
                <MaterialCommunityIcons name="close" size={30} color='blue' />
              </Button>
            </Left>
          </Header>

          <ImageBackground source={{ uri: this.state.newuri }} style={{ margin: 6, flex: 1, width: '100%', height: '100%' }} />



        </Modal>
        <Header style={{ backgroundColor: 'white' }} >
          <Left style={{ flex: 1 }}>
            <Button transparent onPress={() => {
              this.props.navigation.dispatch(DrawerActions.openDrawer())
            }}>
              <Icon name="menu" style={{ color: 'blue', }} />
            </Button>
          </Left>

        </Header>
        <ScrollView refreshControl={
          <RefreshControl refreshing={this.state.refresh} onRefresh={() => {
            this.setState({ refresh: true });
            this.getStuff();
          }}

          />
        }>
          <Body >
            <View style={{ alignSelf: 'center' }}>
              <View style={styles.profileImage}>
                <Image source={{ uri: this.state.image}} style={{width:'100%',height:'100%',}}  />
              </View>

            </View>
            <View style={styles.infoContainer}>
              <Text style={[styles.text, { fontWeight: '200', fontSize: 24 }]}>{this.state.username}</Text>
              <Text style={[styles.text, { color: '#AEB5BC', fontSize: 14 }]}>{this.state.email}</Text>

            </View>
            <View style={styles.statsContainer}>
              <View style={styles.statsBox}>
                <Text style={[styles.text, { fontSize: 24 }]}>{this.state.uploaded}</Text>
                <Text style={[styles.text, styles.subText]}>Images Uploaded</Text>
              </View>
              <View style={[styles.statsBox, { borderColor: '#DFD8C8', borderLeftWidth: 1, borderRightWidth: 1 }]} >
                <Text style={[styles.text, { fontSize: 24 }]} onPress={() => {

                  if (this.state.followers == 0)
                   // ToastAndroid.showWithGravity("No one follows you!", ToastAndroid.SHORT, ToastAndroid.CENTER);
                    Toast.showWithGravity('No one follows you!', Toast.SHORT, Toast.BOTTOM);
                    else
                    this.getfollowers();
                }}>{this.state.followers}</Text>
                <Text style={[styles.text, styles.subText]}>Followers</Text>
              </View>
              <View style={styles.statsBox}>
                <Text style={[styles.text, { fontSize: 24 }]} onPress={async () => {
                  if (this.state.following == 0)
                    //ToastAndroid.showWithGravity("You don't follow anyone!", ToastAndroid.SHORT, ToastAndroid.CENTER);
                    Toast.showWithGravity("You don't follow anyone!", Toast.SHORT, Toast.BOTTOM);
                    else {
                    this.getfollowing();
                  }
                }}>{this.state.following}</Text>
                <Text style={[styles.text, styles.subText]}>Following</Text>
              </View>
            </View>
            <View style={{ marginTop: 32 }}>
              <ScrollView horizontal={true} showsHorizontalScrollIndicator={false}>
                {this.state.imagelist.map((item, index) => {
                  return (
                    <TouchableHighlight onPress={() => this.showPhoto(item.Photo_Uri)}
                      underlayColor={'white'}
                      key={index.toString()+"i"}
                    >
                      <View style={styles.mediaImageContainer} key={index.toString()} >
                        <Image source={{
                          uri:
                            (item.Photo_Uri == 'data:image/png;base64,') ? item.Photo_Uri : UrlOfFile + item.Photo_Uri
                        }}
                          key={index.toString()}
                          style={styles.image}
                          resizeMode="cover"
                        />
                      </View>
                    </TouchableHighlight>
                  )
                })}

              </ScrollView>
            </View>
          </Body>
        </ScrollView>

      </Container >

    )
  }
}
export default class Profile extends Component {
  render() {
    return (
      <Stack.Navigator hideStatusBar={false}  >
        <Stack.Screen name="ProfilePage" component={ProfilePage} options={{
          headerShown: false
        }} />
        <Stack.Screen name="FollowPage" component={FollowPage} options={{
          headerShown: false,
          title: ''
        }} />
        <Stack.Screen name="FollowList" component={FollowList} options={{
          title: ''
        }} />
      </Stack.Navigator>
    )
  }
}
